import React from 'react'
import './App.css';

function App() {
  let [books, setBooks] = React.useState([
    {
      name: '50 Shades of Gray',
      author: 'Dorian Gray',
      price: '50 BGN',
      url: 'data:image/jpeg;base64,',
      description: "And in this quiet moment as I close my eyes, spent and sated, I think I'm in the eye of the storm. And in spite of all he's said, and what he hasn't said, I don't think I have ever been so happy."
    },
    {
      name: 'Amazing Adventures of Spider-Man',
      author: 'Stan Lee',
      price: '60 BGN',
      url: 'data:image/jpeg;base64,',
      description: 'The Amazing Adventures of Spider-Man is a motion-based 3D dark ride at the Islands of Adventure, Orlando and Universal Studios Japan, Osaka theme parks, based on the Marvel Comics superhero Spider-Man.'
    }
  ])
  let [orderedBooks, setOrderedBooks] = React.useState([])

  function addBook() {
    let bookToBeAdded = document.getElementById("bookToBeAdded").value
    let bookAuthor = document.getElementById("bookToBeAddedAuthor").value
    let bookPrice =  document.getElementById("bookToBeAddedPrice").value
    let bookUrl = document.getElementById("bookToBeAddedUrl").value
    let book = {
      name: bookToBeAdded,
      author: bookAuthor,
      price: bookPrice,
      url: bookUrl
    }

    if (bookToBeAdded !== null && bookToBeAdded !== 'undefined' && bookToBeAdded !== "") {
      setBooks(oldBooks => [...oldBooks, book] );
      document.getElementById('bookToBeAdded').value = null
    }    
  }

  function orderBook(e) {
    let book = e.target.previousSibling.textContent
    setBooks(books.filter(item => item !== book))
    setOrderedBooks(oldBooks => [...oldBooks, book])
  }

  let bookTemplate = function(book) {
    return (
    <li>
      <section id="availableBook">
        <h3 id="bookHeader">{book.name} (by {book.author})</h3>
        <article id='bookCover'>
          <img src={book.url} alt="picture of the covers of {book.name}" />
        </article>
        <article id='bookDetails'>
          <p>{book.description}</p>
          <p>{book.price}</p>
          <button onClick={orderBook}>buy</button>
          <button>edit</button>
        </article>
      </section>
    </li>);
  }

  return (
    <div className="App">
      <header>
        <nav>
          <li><a href="#">List books</a></li>
          <li><a href="#">Login</a></li>
          <li><a href="#">Register</a></li>
          <li><a href="#">Add book</a></li>
          <li><a href="#">Order book</a></li>
          <li><a href="#">Orders</a></li>
        </nav>
        <input type="text" name="" id="bookToBeAdded" placeholder="book name"/>
        <input type="text" id="bookToBeAddedAuthor" placeholder="author"/>
        <input type="text" id="bookToBeAddedPrice" placeholder="price"/>
        <input type="text" name="" id="bookToBeAddedUrl" placeholder="url of book cover"/>
        <button onClick={addBook}>add book</button> 
      </header>
      <main>
        <section id="availableBooks">
          
          <h2>Available Books</h2>
          <ul>
            {books.map(book => bookTemplate(book))}
          </ul>
        </section>
        <section id="orderedBooks">
         <h2>Ordered Books</h2>
         <ul>
            {orderedBooks.map(book => <li>{book}</li>)}
          </ul>
        </section>
      </main>
    </div>
  );
}

export default App;
